mapper.select:

SELECT *
FROM
( SELECT list.*, ROWNUM AS RNUM
FROM (



		ORDER BY rsv_number




		ASC


		DESC


) list
)



			RNUM BETWEEN #{start_row} AND #{end_row}


SELECT NVL(count(1), 0) FROM
( SELECT list.*, ROWNUM AS RNUM
FROM(

ORDER BY rsv_number desc
) list
)

SELECT *
FROM RESERVATION
WHERE rsv_number = #{rsv_number}

mapper.update:
UPDATE RESERVATION
SET
rsv_state = #{rsv_state}
where rsv_number = #{rsv_number}
